mapper.select:
select IDX,TITLE,CONTENTS,NAME,DEGREE,FILEPATH,DATE_FORMAT(date, '%Y%m%d') as DATE from spring_pro.board where id =#{id}
AND title LIKE concat('%', #{keyword}, '%')
AND date LIKE concat('%', #{keyword}, '%')
order by idx desc limit #{displayPost, jdbcType=INTEGER}, #{postNum, jdbcType=INTEGER}

select IDX, TITLE, CONTENTS, NAME, DEGREE, FILEPATH, DATE
	from spring_pro.board
	where idx = #{idx}

mapper.update:
update spring_pro.board
	set
		TITLE = #{title},
		CONTENTS = #{contents}
	where idx = #{idx}

mapper.select:
select count(idx) from spring_pro.board where id =#{id}
AND title LIKE concat('%', #{keyword}, '%')
AND date LIKE concat('%', #{keyword}, '%')
